export default class Tab {

	constructor() {
		this.width = uni.upx2px(120);
		this.height = uni.upx2px(120);
		this.left = "auto";
		this.top = 0;
		this.bottom = uni.upx2px(40);
		this.offset = uni.upx2px(0);
		this.viewInstence = null;
		this.defaultBase64 =
			'data:image/png;base64,';
		this.activeBase64 =
			'data:image/png;base64,';
		this.viewDefaultInstence = null;
		this.viewActiveInstence = null;
		this.avaliable = true;
	}
	static getInstance() {
		if (!Tab.instance) {
			Tab.instance = new Tab();
		}
		return Tab.instance;
	}

	create() {
		let that = this;
		this.viewDefaultInstence = new plus.nativeObj.View('defaultIcon', {
			width: `${that.width}px`,
			height: `${that.height+that.offset}px`,
			left: `${Math.ceil((plus.screen.resolutionWidth - that.width) / 2)}px`,
			bottom: '20px'
		});
		let defaultBitmap = new plus.nativeObj.Bitmap('bmp_defaultIcon');
		defaultBitmap.loadBase64Data(that.defaultBase64, function() {
				that.viewDefaultInstence.drawBitmap(defaultBitmap, { //使用canvas画布把图标画到tabbar对应位置上
					tag: 'font',
					id: 'defaultIcon',
					position: {
						width: `${that.width}px`,
						height: `${that.height}px`,
						left: `${that.left}px`,
						top: `${that.top}px`
					}
				});
			},
			function(e) {
				console.log("bitmap load failed:", JSON.stringify(e));
			}
		);

		this.viewActiveInstence = new plus.nativeObj.View('activeIcon', {
			width: `${that.width}px`,
			height: `${that.height+that.offset}px`,
			left: `${Math.ceil((plus.screen.resolutionWidth - that.width) / 2)}px`,
			bottom: '20px'
		});
		let activeBitmap = new plus.nativeObj.Bitmap('bmp_activeIcon');
		activeBitmap.loadBase64Data(that.activeBase64, function() {
				that.viewActiveInstence.drawBitmap(activeBitmap, { //使用canvas画布把图标画到tabbar对应位置上
					tag: 'font',
					id: 'activeIcon',
					position: {
						width: `${that.width}px`,
						height: `${that.height}px`,
						left: `${that.left}px`,
						top: `${that.top}px`
					}
				});
			},
			function(e) {
				console.log("bitmap load failed:", JSON.stringify(e));
			}
		);
	}

	show(isDefault = true,listener=null) {
		if (isDefault) {
			this.viewDefaultInstence.show();
			this.viewActiveInstence.hide();
		} else {
			this.viewDefaultInstence.hide();
			this.viewActiveInstence.show();
		}
	}
	
	setOnClickListener(listener){
		this.viewDefaultInstence.addEventListener('click',function(e){
			listener && listener(e)
		},true);
		this.viewActiveInstence.addEventListener('click',function(e){
			listener && listener(e)
		},true);
	}

}
